        // ----- Core send -----
        private async Task SendAsync()
        {
            if (_cts != null) { _cts.Cancel(); return; }

            var url = txtUrl.Text.Trim();
            if (string.IsNullOrWhiteSpace(url))
            {
                MessageBox.Show("URL ä¸èƒ½ä¸ºç©º", Text, MessageBoxButtons.OK, MessageBoxIcon.Warning);
                return;
            }

            var method = ParseMethod(cboMethod.SelectedItem?.ToString());
            var headers = ParseHeaders(txtHeaders.Text);
            var body = txtBody.Text;

            _cts = new CancellationTokenSource(TimeSpan.FromSeconds((double)nudTimeout.Value));
            btnSend.Text = "Cancel";
            ClearInspector();

            var reqId = Guid.NewGuid().ToString("N")[..8].ToUpperInvariant();

            // â€”â€” Request Log â€”â€”
            LogDivider(rtbReqLog);
            LogSection(rtbReqLog, $"REQUEST  #{reqId}");
            LogInfo(rtbReqLog, $"{method} {url}");
            if (headers.Length > 0)
            {
                LogInfo(rtbReqLog, "Headers:");
                foreach (var (k, v) in headers) LogInfo(rtbReqLog, $"  {k}: {v}");
            }
            if (!string.IsNullOrWhiteSpace(body))
            {
                LogInfo(rtbReqLog, "Body:");
                LogInfo(rtbReqLog, TryFormatJson(body), small: true);
            }
            LogInfo(rtbReqLog, "cURL Preview:");
            LogInfo(rtbReqLog, BuildCurl(url, method, headers, body), small: true);

            var sw = Stopwatch.StartNew();
            try
            {
                var (statusLine, respHeaders, respBody) = await ExecuteAsync(url, method, headers, body, _cts.Token);

                sw.Stop();
                txtRespStatus.Text = statusLine;
                txtRespTime.Text = $"{sw.ElapsedMilliseconds} ms";
                txtRespHeaders.Text = respHeaders;
                txtRespBody.Text = TryFormatJson(respBody);

                // è‡ªåŠ¨å±•å¼€ Inspectorï¼Œç¡®ä¿â€œæœ‰ç»“æœå°±èƒ½çœ‹åˆ°â€
                if (!chkInspector.Checked) chkInspector.Checked = true;

                // â€”â€” Response Log â€”â€”
                LogDivider(rtbRespLog);
                LogSection(rtbRespLog, $"RESPONSE #{reqId}");
                LogInfo(rtbRespLog, $"{statusLine}  ({sw.ElapsedMilliseconds} ms)");
                if (!string.IsNullOrWhiteSpace(respHeaders))
                {
                    LogInfo(rtbRespLog, "Headers:");
                    LogInfo(rtbRespLog, respHeaders, small: true);
                }
                if (!string.IsNullOrWhiteSpace(respBody))
                {
                    LogInfo(rtbRespLog, "Body:");
                    LogInfo(rtbRespLog, TryFormatJson(respBody), small: true);
                }
                else LogWarn(rtbRespLog, "Body is empty.");
            }
            catch (OperationCanceledException)
            {
                sw.Stop();
                txtRespStatus.Text = "Canceled";
                txtRespTime.Text = $"{sw.ElapsedMilliseconds} ms";

                if (!chkInspector.Checked) chkInspector.Checked = true;

                LogDivider(rtbRespLog);
                LogSection(rtbRespLog, $"CANCELED #{reqId}");
                LogWarn(rtbRespLog, $"Request canceled after {sw.ElapsedMilliseconds} ms.");
            }
            catch (Exception ex)
            {
                sw.Stop();
                txtRespStatus.Text = "Error";
                txtRespTime.Text = $"{sw.ElapsedMilliseconds} ms";
                txtRespBody.Text = ex.Message;

                if (!chkInspector.Checked) chkInspector.Checked = true;

                LogDivider(rtbRespLog);
                LogSection(rtbRespLog, $"ERROR    #{reqId}");
                LogError(rtbRespLog, $"{ex.GetType().Name}: {ex.Message}");
                if (ex.StackTrace != null) LogError(rtbRespLog, ex.StackTrace, small: true);
            }
            finally
            {
                _cts?.Dispose();
                _cts = null;
                btnSend.Text = "Send";
            }
        }

        private static Method ParseMethod(string? text) =>
            text switch
            {
                "GET" => Method.Get,
                "PUT" => Method.Put,
                "PATCH" => Method.Patch,
                "DELETE" => Method.Delete,
                _ => Method.Post
            };

        private static (string key, string value)[] ParseHeaders(string raw) =>
            raw.Replace("\r", string.Empty)
               .Split('\n')
               .Select(l => l.Trim())
               .Where(l => !string.IsNullOrWhiteSpace(l))
               .Select(l =>
               {
                   var idx = l.IndexOf(':');
                   if (idx <= 0) return (string.Empty, string.Empty);
                   return (l[..idx].Trim(), l[(idx + 1)..].Trim());
               })
               .Where(p => !string.IsNullOrWhiteSpace(p.Item1))
               .ToArray();

        private async Task<(string statusLine, string headers, string body)> ExecuteAsync(
            string url, Method method, (string key, string value)[] headers, string body, CancellationToken ct)
        {
            var options = new RestClientOptions
            {
                ThrowOnAnyError = false,
                MaxTimeout = (int)TimeSpan.FromSeconds((double)nudTimeout.Value).TotalMilliseconds
            };

            var client = new RestClient(options);
            var request = new RestRequest(url, method);

            foreach (var h in headers) request.AddHeader(h.key, h.value);
            if (method != Method.Get && !string.IsNullOrWhiteSpace(body))
                request.AddStringBody(body, ContentType.Json);

            var response = await client.ExecuteAsync(request, ct);

            var reason = string.IsNullOrWhiteSpace(response.StatusDescription)
                ? response.StatusCode.ToString()
                : response.StatusDescription;

            var statusLine = $"{(int)response.StatusCode} {reason}";
            var headerText = string.Join(Environment.NewLine, response.Headers.Select(h => $"{h.Name}: {h.Value}"));
            var respBody = response.Content ?? string.Empty;

            return (statusLine, headerText, respBody);
        }

        private void ClearInspector()
        {
            txtRespStatus.Text = string.Empty;
            txtRespTime.Text = string.Empty;
            txtRespHeaders.Text = string.Empty;
            txtRespBody.Text = string.Empty;
        }

        // ----- Log helpers -----
        private void LogDivider(RichTextBox target) => AppendLog(target, new string('â”€', 80), Color.Gray);

        private void LogSection(RichTextBox target, string title) =>
            AppendLog(target, $"[{DateTime.Now:HH:mm:ss.fff}] {title}", _darkMode ? Color.LightSkyBlue : Color.RoyalBlue, bold: true);

        private void LogInfo(RichTextBox target, string message, bool small = false) =>
            AppendLog(target, message, _darkMode ? Color.Gainsboro : Color.Black, small: small);

        private void LogWarn(RichTextBox target, string message, bool small = false) =>
            AppendLog(target, message, _darkMode ? Color.Khaki : Color.DarkOrange, small: small);

        private void LogError(RichTextBox target, string message, bool small = false) =>
            AppendLog(target, message, _darkMode ? Color.LightPink : Color.Crimson, bold: !small, small: small);

        private void AppendLog(RichTextBox target, string text, Color? color = null, bool bold = false, bool small = false)
        {
            if (string.IsNullOrEmpty(text)) return;

            void Append()
            {
                if (target.TextLength > MaxLogChars)
                {
                    target.Select(0, target.TextLength / 3);
                    target.SelectedText = string.Empty;
                }

                var start = target.TextLength;
                var prefix = text.StartsWith("[") ? "" : $"[{DateTime.Now:HH:mm:ss.fff}] ";
                target.AppendText(prefix + text + Environment.NewLine);
                target.Select(start, (prefix + text).Length);

                if (color.HasValue) target.SelectionColor = color.Value;
                if (bold || small)
                {
                    var f = target.SelectionFont ?? target.Font;
                    var style = bold ? FontStyle.Bold : FontStyle.Regular;
                    var size = small ? Math.Max(8f, f.Size - 1f) : f.Size;
                    target.SelectionFont = new Font(f, style);
                    if (small) target.SelectionFont = new Font(target.SelectionFont.FontFamily, size, style);
                }

                target.SelectionStart = target.TextLength;
                target.ScrollToCaret();
            }

            if (target.InvokeRequired) target.Invoke((Action)Append);
            else Append();
        }

        // ----- Text helpers -----
        private string TryFormatJson(string text)
        {
            if (string.IsNullOrWhiteSpace(text)) return string.Empty;
            try
            {
                using var doc = JsonDocument.Parse(text);
                var opts = new JsonWriterOptions
                {
                    Indented = true,
                    Encoder = System.Text.Encodings.Web.JavaScriptEncoder.UnsafeRelaxedJsonEscaping
                };
                using var stream = new System.IO.MemoryStream();
                using (var writer = new Utf8JsonWriter(stream, opts)) doc.WriteTo(writer);
                return Encoding.UTF8.GetString(stream.ToArray());
            }
            catch { return text; }
        }

        private string BuildCurl(string url, Method method, (string key, string value)[] headers, string body)
        {
            var sb = new StringBuilder("curl");
            sb.Append(" -X ").Append(method.ToString().ToUpperInvariant());
            foreach (var (key, value) in headers)
                sb.Append(" -H ").Append('"').Append(key.Replace("\"", "\\\"")).Append(": ").Append(value.Replace("\"", "\\\"")).Append('"');
            if (method != Method.Get && !string.IsNullOrWhiteSpace(body))
                sb.Append(" --data ").Append('"').Append(body.Replace("\"", "\\\"").Replace("\r", "").Replace("\n", "")).Append('"');
            sb.Append(' ').Append('"').Append(url).Append('"');
            return sb.ToString();
        }
    }

