using System;
using System.Threading.Tasks;
using System.Windows.Forms;

namespace WinFormsDemo
{
    public class MainForm : Form
    {
        private readonly Label labelMessage;

        public MainForm()
        {
            // è®¾ç½®çª—ä½“é»˜è®¤å±…ä¸­
            StartPosition = FormStartPosition.CenterScreen;
            Width = 800;
            Height = 500;

            // åˆå§‹åŒ– Label
            labelMessage = new Label
            {
                Dock = DockStyle.Fill,
                Font = new System.Drawing.Font("Segoe UI", 14), // å­—ä½“ç¼©å°ä¸€ç‚¹
                TextAlign = System.Drawing.ContentAlignment.MiddleCenter
            };

            Controls.Add(labelMessage);

            // çª—ä½“åŠ è½½æ—¶å¯åŠ¨æ–‡å­—æ˜¾ç¤º
            Load += async (_, __) => await ShowMessageAsync();
        }

        private async Task ShowMessageAsync()
        {
            string message = "Hello, SharpPad! å…³æ³¨æˆ‘: https://github.com/gaoconggit/SharpPad";
            foreach (char c in message)
            {
                labelMessage.Text += c;
                await Task.Delay(100);
            }
        }

        [STAThread]
        public static void Main()
        {
            Application.EnableVisualStyles();
            Application.SetCompatibleTextRenderingDefault(false);
            Application.Run(new MainForm());
        }
    }
}

using System;
using System.Collections.Generic;
using System.IdentityModel.Tokens.Jwt;
using System.Linq;
using System.Windows.Forms;
using Newtonsoft.Json;

public class MainForm : Form
{
    private TextBox txtToken;
    private Button btnParse;
    private Button btnClear;
    private RichTextBox rtbResult;

    public MainForm()
    {
        this.Text = "JWT Token è§£æå™¨";
        this.Width = 800;
        this.Height = 600;
        this.StartPosition = FormStartPosition.CenterScreen;   // çª—ä½“å±…ä¸­
        this.FormBorderStyle = FormBorderStyle.FixedDialog;    // å›ºå®šå¤§å°
        this.MaximizeBox = false;                              // ç¦æ­¢æœ€å¤§åŒ–
        this.Font = new System.Drawing.Font("Segoe UI", 10);   // ç»Ÿä¸€å­—ä½“

        Label lbl = new Label
        {
            Text = "è¯·è¾“å…¥ JWT Token:",
            AutoSize = true,
            Top = 20,
            Left = 20
        };
        this.Controls.Add(lbl);

        txtToken = new TextBox
        {
            Top = lbl.Bottom + 5,
            Left = 20,
            Width = 740,
            Height = 60,                        // æ›´é«˜
            Multiline = true,                   // å¤šè¡Œè¾“å…¥
            ScrollBars = ScrollBars.Vertical,   // æ»šåŠ¨æ¡
            Font = new System.Drawing.Font("Segoe UI", 10)
        };
        this.Controls.Add(txtToken);

        btnParse = new Button
        {
            Text = "è§£æ",
            Top = txtToken.Bottom + 10,
            Left = 20,
            Width = 100,
            Height = 35
        };
        btnParse.Click += BtnParse_Click;
        this.Controls.Add(btnParse);

        btnClear = new Button
        {
            Text = "æ¸…ç©º",
            Top = txtToken.Bottom + 10,
            Left = btnParse.Right + 10,
            Width = 100,
            Height = 35
        };
        btnClear.Click += (s, e) => { txtToken.Clear(); rtbResult.Clear(); };
        this.Controls.Add(btnClear);

        rtbResult = new RichTextBox
        {
            Top = btnParse.Bottom + 15,
            Left = 20,
            Width = 740,
            Height = 400,
            ReadOnly = true,
            Font = new System.Drawing.Font("Consolas", 10), // ç­‰å®½å­—ä½“
            ScrollBars = RichTextBoxScrollBars.Both,
            WordWrap = false
        };
        this.Controls.Add(rtbResult);
    }

    private void BtnParse_Click(object sender, EventArgs e)
    {
        string token = txtToken.Text.Trim();
        if (string.IsNullOrEmpty(token))
        {
            MessageBox.Show("è¯·è¾“å…¥ JWT Tokenã€‚", "æç¤º", MessageBoxButtons.OK, MessageBoxIcon.Information);
            return;
        }

        try
        {
            var fieldOrder = new Dictionary<string, int>()
            {
                { "exp", 0 },
                { "iss", 1 },
                { "aud", 2 },
                { "client_id", 3 },
                { "sub", 4 },
                { "auth_time", 5 },
                { "idp", 6 },
                { "userid", 7 },
                { "tenantid", 8 },
                { "tenantname", 9 },
                { "scope", 10 },
            };

            JwtSecurityTokenHandler tokenHandler = new JwtSecurityTokenHandler();
            JwtSecurityToken jwtToken = tokenHandler.ReadJwtToken(token);

            rtbResult.Clear();
            rtbResult.AppendText("=== Header ===\n");
            rtbResult.AppendText(JsonConvert.SerializeObject(jwtToken.Header, Formatting.Indented));
            rtbResult.AppendText("\n\n");

            rtbResult.AppendText("=== Payload ===\n");
            var payloadDict = new Dictionary<string, object>();
            foreach (var claim in jwtToken.Payload.Claims)
            {
                if (payloadDict.ContainsKey(claim.Type))
                {
                    if (payloadDict[claim.Type] is object[] valueArray)
                    {
                        payloadDict[claim.Type] = valueArray.Append(claim.Value).ToArray();
                    }
                    else
                    {
                        payloadDict[claim.Type] = new object[] { payloadDict[claim.Type], claim.Value };
                    }
                }
                else
                {
                    payloadDict.Add(claim.Type, claim.Value);
                }
            }

            var sortedDict = payloadDict
                .OrderBy(x => fieldOrder.ContainsKey(x.Key) ? fieldOrder[x.Key] : int.MaxValue)
                .ToDictionary(x => x.Key, x => x.Value);

            var json = JsonConvert.SerializeObject(sortedDict, Formatting.Indented);
            rtbResult.AppendText(json);
        }
        catch (Exception ex)
        {
            MessageBox.Show("è§£æå¤±è´¥: " + ex.Message, "é”™è¯¯", MessageBoxButtons.OK, MessageBoxIcon.Error);
        }
    }

    [STAThread]
    public static void Main()
    {
        Application.EnableVisualStyles();
        Application.SetCompatibleTextRenderingDefault(false);
        Application.Run(new MainForm());
    }
}

// Program.cs
// éœ€è¦ NuGet: RestSharpï¼ˆç¤ºä¾‹åŸºäº 112.xï¼‰
// å•æ–‡ä»¶ WinFormsï¼Œå¯ç›´æ¥ç¼–è¯‘è¿è¡Œ

using System;
using System.Diagnostics;
using System.Drawing;
using System.Linq;
using System.Text;
using System.Text.Json;
using System.Threading;
using System.Threading.Tasks;
using System.Windows.Forms;
using RestSharp;

namespace HttpJsonTester
{
    public sealed class MainForm : Form
    {
        // --- Top bar ---
        private readonly TextBox txtUrl = new TextBox { Anchor = AnchorStyles.Left | AnchorStyles.Right };
        private readonly ComboBox cboMethod = new ComboBox { DropDownStyle = ComboBoxStyle.DropDownList };
        private readonly NumericUpDown nudTimeout = new NumericUpDown { Minimum = 1, Maximum = 300, Value = 30 };
        private readonly Button btnSend = new Button { Text = "Send" };
        private readonly Button btnClear = new Button { Text = "Clear" };
        private readonly Button btnTheme = new Button { Text = "ğŸŒ™ Dark Mode" };
        private readonly CheckBox chkInspector = new CheckBox
        {
            Appearance = Appearance.Button,
            AutoSize = true,
            Text = "Inspector",
            Checked = false // é»˜è®¤æŠ˜å å³ä¾§æ£€æŸ¥å™¨
        };

        // --- Request inputs (left) ---
        private readonly TextBox txtHeaders = new TextBox { Multiline = true, ScrollBars = ScrollBars.Vertical };
        private readonly TextBox txtBody = new TextBox { Multiline = true, ScrollBars = ScrollBars.Both, WordWrap = false };

        // --- Logs (bottom tabs) ---
        private readonly RichTextBox rtbReqLog = new RichTextBox { ReadOnly = true, DetectUrls = false };
        private readonly RichTextBox rtbRespLog = new RichTextBox { ReadOnly = true, DetectUrls = false };

        // --- Inspector (right) ---
        private readonly TextBox txtRespStatus = new TextBox { ReadOnly = true };
        private readonly TextBox txtRespTime = new TextBox { ReadOnly = true };
        private readonly TextBox txtRespHeaders = new TextBox { Multiline = true, ReadOnly = true, ScrollBars = ScrollBars.Vertical };
        private readonly TextBox txtRespBody = new TextBox { Multiline = true, ReadOnly = true, ScrollBars = ScrollBars.Both, WordWrap = false };

        private SplitContainer _split = null!;
        private CancellationTokenSource? _cts;
        private bool _darkMode = false;

        // Theme
        private readonly Color themeColor = Color.FromArgb(52, 152, 219);
        private readonly Color themeDark = Color.FromArgb(41, 128, 185);

        // Log guard
        private const int MaxLogChars = 1_000_000; // é˜²å¡é¡¿ï¼šæœ€å¤šä¿ç•™ 100 ä¸‡å­—ç¬¦

        public MainForm()
        {
            Text = "Post JSON Tester (.NET 8 + RestSharp)";
            Width = 1200;
            Height = 750;
            StartPosition = FormStartPosition.CenterScreen;
            Font = new Font("Segoe UI", 10F, FontStyle.Regular);

            // Top bar styling
            StyleButton(btnSend);
            StyleButton(btnClear);
            StyleButton(btnTheme);

            cboMethod.Items.AddRange(new object[] { "POST", "GET", "PUT", "PATCH", "DELETE" });
            cboMethod.SelectedIndex = 0;

            var lblUrl = new Label { Text = "URL", AutoSize = true, Anchor = AnchorStyles.Left };
            var lblMethod = new Label { Text = "Method", AutoSize = true, Anchor = AnchorStyles.Left };
            var lblTimeout = new Label { Text = "Timeout(s)", AutoSize = true, Anchor = AnchorStyles.Left };

            var top = new TableLayoutPanel
            {
                Dock = DockStyle.Top,
                Height = 42,
                BackColor = Color.FromArgb(245, 245, 245),
                Padding = new Padding(6),
                ColumnCount = 10
            };
            top.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));                 // URL label
            top.ColumnStyles.Add(new ColumnStyle(SizeType.Percent, 100));             // URL
            top.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));                 // Method label
            top.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));                 // Method
            top.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));                 // Timeout label
            top.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));                 // Timeout
            top.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));                 // Send
            top.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));                 // Clear
            top.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));                 // Theme
            top.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));                 // Inspector toggle

            top.Controls.Add(lblUrl, 0, 0);
            top.Controls.Add(txtUrl, 1, 0);
            top.Controls.Add(lblMethod, 2, 0);
            top.Controls.Add(cboMethod, 3, 0);
            top.Controls.Add(lblTimeout, 4, 0);
            top.Controls.Add(nudTimeout, 5, 0);
            top.Controls.Add(btnSend, 6, 0);
            top.Controls.Add(btnClear, 7, 0);
            top.Controls.Add(btnTheme, 8, 0);
            top.Controls.Add(chkInspector, 9, 0);

            // Left: headers + body
            GroupBox MakeGroup(string title, Control inner, int height = 0)
            {
                var grp = new GroupBox
                {
                    Text = title,
                    Dock = height > 0 ? DockStyle.Top : DockStyle.Fill,
                    Height = height,
                    Padding = new Padding(10),
                    Font = new Font("Segoe UI", 9.5F, FontStyle.Bold)
                };
                inner.Dock = DockStyle.Fill;
                grp.Controls.Add(inner);
                return grp;
            }
            var grpHeaders = MakeGroup("Headers (key: value per line)", txtHeaders, 120);
            var grpBody = MakeGroup("Body (JSON)", txtBody);

            var leftPanel = new Panel { Dock = DockStyle.Fill, Padding = new Padding(6) };
            leftPanel.Controls.Add(grpBody);
            leftPanel.Controls.Add(grpHeaders);

            // Right: Inspector (Response)
            var grpResp = MakeGroup("Response", new Panel());
            var inspector = new TableLayoutPanel { Dock = DockStyle.Fill, ColumnCount = 2, RowCount = 4, Padding = new Padding(4) };
            inspector.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));
            inspector.ColumnStyles.Add(new ColumnStyle(SizeType.Percent, 100));
            void AddRow(string label, Control ctrl, int row)
            {
                inspector.Controls.Add(new Label { Text = label, AutoSize = true, Font = new Font("Segoe UI", 9F, FontStyle.Bold) }, 0, row);
                inspector.Controls.Add(ctrl, 1, row);
            }
            AddRow("Status", txtRespStatus, 0);
            AddRow("Elapsed", txtRespTime, 1);
            AddRow("Headers", txtRespHeaders, 2);
            AddRow("Body", txtRespBody, 3);
            grpResp.Controls.Add(inspector);

            // Split: left + inspector right
            _split = new SplitContainer { Dock = DockStyle.Fill, SplitterDistance = 560 };
            _split.Panel1.Controls.Add(leftPanel);
            _split.Panel2.Controls.Add(grpResp);
            _split.Panel2Collapsed = true; // é»˜è®¤æŠ˜å 

            // Bottom tabs: request/response logs
            var logsTabs = new TabControl { Dock = DockStyle.Bottom, Height = 230 };
            var tpReq = new TabPage("Request Log") { Padding = new Padding(6) };
            var tpResp = new TabPage("Response Log") { Padding = new Padding(6) };
            rtbReqLog.Dock = DockStyle.Fill;
            rtbRespLog.Dock = DockStyle.Fill;
            tpReq.Controls.Add(rtbReqLog);
            tpResp.Controls.Add(rtbRespLog);
            logsTabs.TabPages.Add(tpReq);
            logsTabs.TabPages.Add(tpResp);

            // Compose
            Controls.Add(_split);
            Controls.Add(logsTabs);
            Controls.Add(top);

            // Events
            btnSend.Click += async (_, __) => await SendAsync();
            btnClear.Click += (_, __) => { rtbReqLog.Clear(); rtbRespLog.Clear(); };
            btnTheme.Click += (_, __) => ToggleTheme();
            chkInspector.CheckedChanged += (_, __) =>
            {
                _split.Panel2Collapsed = !chkInspector.Checked;
                if (!_split.Panel2Collapsed)
                {
                    // å±•å¼€æ—¶ç»™å³ä¾§ç•™å®½åº¦ï¼Œé¿å…è§†è§‰ä¸Šåƒâ€œæ²¡ååº”â€
                    _split.SplitterDistance = Math.Max(420, Width - 520);
                }
            };

            // Defaults
            txtUrl.Text = "https://httpbin.org/anything";
            txtBody.Text = "{\r\n  \"hello\": \"world\"\r\n}";
            txtHeaders.Text = "Content-Type: application/json";

            ApplyTheme();
        }

        // ----- UI helpers -----
        private void StyleButton(Button btn)
        {
            btn.FlatStyle = FlatStyle.Flat;
            btn.FlatAppearance.BorderSize = 0;
            btn.Height = 32;
            btn.Margin = new Padding(6, 0, 6, 0);
            btn.Padding = new Padding(8, 0, 8, 0);
            btn.Cursor = Cursors.Hand;
        }

        private void ToggleTheme()
        {
            _darkMode = !_darkMode;
            btnTheme.Text = _darkMode ? "â˜€ï¸ Light Mode" : "ğŸŒ™ Dark Mode";
            ApplyTheme();
        }

        private void ApplyTheme()
        {
            if (_darkMode)
            {
                BackColor = Color.FromArgb(45, 45, 48);
                ForeColor = Color.White;
                foreach (Control c in Controls) ApplyDark(c);
            }
            else
            {
                BackColor = Color.White;
                ForeColor = Color.Black;
                foreach (Control c in Controls) ApplyLight(c);
            }

            rtbReqLog.BackColor = _darkMode ? Color.FromArgb(30, 30, 30) : Color.White;
            rtbReqLog.ForeColor = _darkMode ? Color.Gainsboro : Color.Black;
            rtbRespLog.BackColor = rtbReqLog.BackColor;
            rtbRespLog.ForeColor = rtbReqLog.ForeColor;

            // Inspector toggle style
            chkInspector.BackColor = _darkMode ? Color.Black : themeColor;
            chkInspector.ForeColor = _darkMode ? themeColor : Color.White;
        }

        private void ApplyDark(Control ctrl)
        {
            ctrl.BackColor = Color.FromArgb(45, 45, 48);
            ctrl.ForeColor = Color.White;
            if (ctrl is Button b) { b.BackColor = Color.Black; b.ForeColor = themeColor; }
            foreach (Control child in ctrl.Controls) ApplyDark(child);
        }

        private void ApplyLight(Control ctrl)
        {
            ctrl.BackColor = Color.White;
            ctrl.ForeColor = Color.Black;
            if (ctrl is Button b) { b.BackColor = themeColor; b.ForeColor = Color.White; }
            foreach (Control child in ctrl.Controls) ApplyLight(child);
        }

        // ----- Core send -----
        private async Task SendAsync()
        {
            if (_cts != null) { _cts.Cancel(); return; }

            var url = txtUrl.Text.Trim();
            if (string.IsNullOrWhiteSpace(url))
            {
                MessageBox.Show("URL ä¸èƒ½ä¸ºç©º", Text, MessageBoxButtons.OK, MessageBoxIcon.Warning);
                return;
            }

            var method = ParseMethod(cboMethod.SelectedItem?.ToString());
            var headers = ParseHeaders(txtHeaders.Text);
            var body = txtBody.Text;

            _cts = new CancellationTokenSource(TimeSpan.FromSeconds((double)nudTimeout.Value));
            btnSend.Text = "Cancel";
            ClearInspector();

            var reqId = Guid.NewGuid().ToString("N")[..8].ToUpperInvariant();

            // â€”â€” Request Log â€”â€”
            LogDivider(rtbReqLog);
            LogSection(rtbReqLog, $"REQUEST  #{reqId}");
            LogInfo(rtbReqLog, $"{method} {url}");
            if (headers.Length > 0)
            {
                LogInfo(rtbReqLog, "Headers:");
                foreach (var (k, v) in headers) LogInfo(rtbReqLog, $"  {k}: {v}");
            }
            if (!string.IsNullOrWhiteSpace(body))
            {
                LogInfo(rtbReqLog, "Body:");
                LogInfo(rtbReqLog, TryFormatJson(body), small: true);
            }
            LogInfo(rtbReqLog, "cURL Preview:");
            LogInfo(rtbReqLog, BuildCurl(url, method, headers, body), small: true);

            var sw = Stopwatch.StartNew();
            try
            {
                var (statusLine, respHeaders, respBody) = await ExecuteAsync(url, method, headers, body, _cts.Token);

                sw.Stop();
                txtRespStatus.Text = statusLine;
                txtRespTime.Text = $"{sw.ElapsedMilliseconds} ms";
                txtRespHeaders.Text = respHeaders;
                txtRespBody.Text = TryFormatJson(respBody);

                // è‡ªåŠ¨å±•å¼€ Inspectorï¼Œç¡®ä¿â€œæœ‰ç»“æœå°±èƒ½çœ‹åˆ°â€
                if (!chkInspector.Checked) chkInspector.Checked = true;

                // â€”â€” Response Log â€”â€”
                LogDivider(rtbRespLog);
                LogSection(rtbRespLog, $"RESPONSE #{reqId}");
                LogInfo(rtbRespLog, $"{statusLine}  ({sw.ElapsedMilliseconds} ms)");
                if (!string.IsNullOrWhiteSpace(respHeaders))
                {
                    LogInfo(rtbRespLog, "Headers:");
                    LogInfo(rtbRespLog, respHeaders, small: true);
                }
                if (!string.IsNullOrWhiteSpace(respBody))
                {
                    LogInfo(rtbRespLog, "Body:");
                    LogInfo(rtbRespLog, TryFormatJson(respBody), small: true);
                }
                else LogWarn(rtbRespLog, "Body is empty.");
            }
            catch (OperationCanceledException)
            {
                sw.Stop();
                txtRespStatus.Text = "Canceled";
                txtRespTime.Text = $"{sw.ElapsedMilliseconds} ms";

                if (!chkInspector.Checked) chkInspector.Checked = true;

                LogDivider(rtbRespLog);
                LogSection(rtbRespLog, $"CANCELED #{reqId}");
                LogWarn(rtbRespLog, $"Request canceled after {sw.ElapsedMilliseconds} ms.");
            }
            catch (Exception ex)
            {
                sw.Stop();
                txtRespStatus.Text = "Error";
                txtRespTime.Text = $"{sw.ElapsedMilliseconds} ms";
                txtRespBody.Text = ex.Message;

                if (!chkInspector.Checked) chkInspector.Checked = true;

                LogDivider(rtbRespLog);
                LogSection(rtbRespLog, $"ERROR    #{reqId}");
                LogError(rtbRespLog, $"{ex.GetType().Name}: {ex.Message}");
                if (ex.StackTrace != null) LogError(rtbRespLog, ex.StackTrace, small: true);
            }
            finally
            {
                _cts?.Dispose();
                _cts = null;
                btnSend.Text = "Send";
            }
        }

        private static Method ParseMethod(string? text) =>
            text switch
            {
                "GET" => Method.Get,
                "PUT" => Method.Put,
                "PATCH" => Method.Patch,
                "DELETE" => Method.Delete,
                _ => Method.Post
            };

        private static (string key, string value)[] ParseHeaders(string raw) =>
            raw.Replace("\r", string.Empty)
               .Split('\n')
               .Select(l => l.Trim())
               .Where(l => !string.IsNullOrWhiteSpace(l))
               .Select(l =>
               {
                   var idx = l.IndexOf(':');
                   if (idx <= 0) return (string.Empty, string.Empty);
                   return (l[..idx].Trim(), l[(idx + 1)..].Trim());
               })
               .Where(p => !string.IsNullOrWhiteSpace(p.Item1))
               .ToArray();

        private async Task<(string statusLine, string headers, string body)> ExecuteAsync(
            string url, Method method, (string key, string value)[] headers, string body, CancellationToken ct)
        {
            var options = new RestClientOptions
            {
                ThrowOnAnyError = false,
                MaxTimeout = (int)TimeSpan.FromSeconds((double)nudTimeout.Value).TotalMilliseconds
            };

            var client = new RestClient(options);
            var request = new RestRequest(url, method);

            foreach (var h in headers) request.AddHeader(h.key, h.value);
            if (method != Method.Get && !string.IsNullOrWhiteSpace(body))
                request.AddStringBody(body, ContentType.Json);

            var response = await client.ExecuteAsync(request, ct);

            var reason = string.IsNullOrWhiteSpace(response.StatusDescription)
                ? response.StatusCode.ToString()
                : response.StatusDescription;

            var statusLine = $"{(int)response.StatusCode} {reason}";
            var headerText = string.Join(Environment.NewLine, response.Headers.Select(h => $"{h.Name}: {h.Value}"));
            var respBody = response.Content ?? string.Empty;

            return (statusLine, headerText, respBody);
        }

        private void ClearInspector()
        {
            txtRespStatus.Text = string.Empty;
            txtRespTime.Text = string.Empty;
            txtRespHeaders.Text = string.Empty;
            txtRespBody.Text = string.Empty;
        }

        // ----- Log helpers -----
        private void LogDivider(RichTextBox target) => AppendLog(target, new string('â”€', 80), Color.Gray);

        private void LogSection(RichTextBox target, string title) =>
            AppendLog(target, $"[{DateTime.Now:HH:mm:ss.fff}] {title}", _darkMode ? Color.LightSkyBlue : Color.RoyalBlue, bold: true);

        private void LogInfo(RichTextBox target, string message, bool small = false) =>
            AppendLog(target, message, _darkMode ? Color.Gainsboro : Color.Black, small: small);

        private void LogWarn(RichTextBox target, string message, bool small = false) =>
            AppendLog(target, message, _darkMode ? Color.Khaki : Color.DarkOrange, small: small);

        private void LogError(RichTextBox target, string message, bool small = false) =>
            AppendLog(target, message, _darkMode ? Color.LightPink : Color.Crimson, bold: !small, small: small);

        private void AppendLog(RichTextBox target, string text, Color? color = null, bool bold = false, bool small = false)
        {
            if (string.IsNullOrEmpty(text)) return;

            void Append()
            {
                if (target.TextLength > MaxLogChars)
                {
                    target.Select(0, target.TextLength / 3);
                    target.SelectedText = string.Empty;
                }

                var start = target.TextLength;
                var prefix = text.StartsWith("[") ? "" : $"[{DateTime.Now:HH:mm:ss.fff}] ";
                target.AppendText(prefix + text + Environment.NewLine);
                target.Select(start, (prefix + text).Length);

                if (color.HasValue) target.SelectionColor = color.Value;
                if (bold || small)
                {
                    var f = target.SelectionFont ?? target.Font;
                    var style = bold ? FontStyle.Bold : FontStyle.Regular;
                    var size = small ? Math.Max(8f, f.Size - 1f) : f.Size;
                    target.SelectionFont = new Font(f, style);
                    if (small) target.SelectionFont = new Font(target.SelectionFont.FontFamily, size, style);
                }

                target.SelectionStart = target.TextLength;
                target.ScrollToCaret();
            }

            if (target.InvokeRequired) target.Invoke((Action)Append);
            else Append();
        }

        // ----- Text helpers -----
        private string TryFormatJson(string text)
        {
            if (string.IsNullOrWhiteSpace(text)) return string.Empty;
            try
            {
                using var doc = JsonDocument.Parse(text);
                var opts = new JsonWriterOptions
                {
                    Indented = true,
                    Encoder = System.Text.Encodings.Web.JavaScriptEncoder.UnsafeRelaxedJsonEscaping
                };
                using var stream = new System.IO.MemoryStream();
                using (var writer = new Utf8JsonWriter(stream, opts)) doc.WriteTo(writer);
                return Encoding.UTF8.GetString(stream.ToArray());
            }
            catch { return text; }
        }

        private string BuildCurl(string url, Method method, (string key, string value)[] headers, string body)
        {
            var sb = new StringBuilder("curl");
            sb.Append(" -X ").Append(method.ToString().ToUpperInvariant());
            foreach (var (key, value) in headers)
                sb.Append(" -H ").Append('"').Append(key.Replace("\"", "\\\"")).Append(": ").Append(value.Replace("\"", "\\\"")).Append('"');
            if (method != Method.Get && !string.IsNullOrWhiteSpace(body))
                sb.Append(" --data ").Append('"').Append(body.Replace("\"", "\\\"").Replace("\r", "").Replace("\n", "")).Append('"');
            sb.Append(' ').Append('"').Append(url).Append('"');
            return sb.ToString();
        }
    }

