using System;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Threading.Tasks;
using Newtonsoft.Json;
using System.Collections.Generic;
using System.IdentityModel.Tokens.Jwt;
using System.Linq;

class Program
{
    public static async Task Main()
    {
        string token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiIxMjM0NTY3ODkwIiwibmFtZSI6IkpvaG4gRG9lIiwiaWF0IjoxNTE2MjM5MDIyfQ.SflKxwRJSMeKKF2QT4fwpMeJf36POk6yJV_adQssw5c";

        // å®šä¹‰ä¸€ä¸ªå­—æ®µæ’åºé¡ºåºå­—å…¸ 
        var fieldOrder = new Dictionary<string, int>()
        {
            { "exp", 0 },
            { "iss", 1 },
            { "aud", 2 },
            { "client_id", 3 },
            { "sub", 4 },
            { "auth_time", 5 },
            { "idp", 6 },
            { "userid", 7 },
            { "tenantid", 8 },
            { "tenantname", 9 },
            { "scope", 10 },
        };

        JwtSecurityTokenHandler tokenHandler = new JwtSecurityTokenHandler();
        JwtSecurityToken jwtToken = tokenHandler.ReadJwtToken(token);

        Console.WriteLine("```json Header:");
        JsonConvert.SerializeObject(jwtToken.Header, Newtonsoft.Json.Formatting.Indented).Dump();
        Console.WriteLine("```");

        Console.WriteLine("```json Payload:");
        // å°†JWTä»¤ç‰Œçš„è´Ÿè½½è§£æä¸ºå­—å…¸ç±»å‹
        var payloadDict = new Dictionary<string, object>();
        foreach (var claim in jwtToken.Payload.Claims)
        {
            if (payloadDict.ContainsKey(claim.Type))
            {
                // å¦‚æœé”®å·²ç»å­˜åœ¨ï¼Œå°è¯•å°†å…¶å€¼è§£æä¸ºæ•°ç»„å¹¶è¿½åŠ æ–°å€¼ï¼Œå¦åˆ™æ‰§è¡Œæ·»åŠ æ“ä½œ
                if (payloadDict[claim.Type] is object[] valueArray)
                {
                    payloadDict[claim.Type] = valueArray.Append(claim.Value).ToArray();
                }
                else
                {
                    payloadDict[claim.Type] = new object[] { payloadDict[claim.Type], claim.Value };
                }
            }
            else
            {
                payloadDict.Add(claim.Type, claim.Value);
            }
        }

        // å°†å­—å…¸æŒ‰ç…§é¢„å®šä¹‰çš„é¡ºåºæ’åº
        var sortedDict = payloadDict.OrderBy(x => fieldOrder.ContainsKey(x.Key) ? fieldOrder[x.Key] : int.MaxValue)
                                     .ToDictionary(x => x.Key, x => x.Value);

        // è¾“å‡ºJWTä»¤ç‰Œçš„è´Ÿè½½éƒ¨åˆ†
        var json = JsonConvert.SerializeObject(sortedDict, Newtonsoft.Json.Formatting.Indented);
        Console.WriteLine(json);

    }
}

using System;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Threading.Tasks;
using Newtonsoft.Json;
using System.Text;

namespace login
{
    public class Program
    {
        public async static Task Main()
        {
            var url = "ä½ çš„url";

            var body = """
                   {

                   }
                """;

            await PostJsonAsync(url, body, showReqHeaders: false, showResHeaders: false);
        }

        #region PostJsonAsync
        public static async Task PostJsonAsync(string url, string reqJson, bool showReqHeaders = false, bool showResHeaders = false)
        {
            using var client = new HttpClient();

            var request = new HttpRequestMessage(HttpMethod.Post, url)
            {
                Content = new StringContent(reqJson, Encoding.UTF8, "application/json")
            };

            // è®¾ç½®è¯·æ±‚å¤´ï¼ˆå¦‚æœéœ€è¦æ·»åŠ å…¶ä»–çš„å¤´ï¼Œä¹Ÿåœ¨ä¸‹é¢æ·»åŠ ï¼‰
            request.Headers.Accept.Add(new MediaTypeWithQualityHeaderValue("application/json"));

            if (showReqHeaders)
            {
                // è®°å½•è¯·æ±‚å¤´
                Console.WriteLine("Request Headers:\n");
                Console.WriteLine("```json\n");

                // æ·»åŠ  URL å’Œ HTTP Method
                Console.WriteLine($"{request.Method} {url}");
                foreach (var header in request.Headers)
                {
                    Console.WriteLine($"{header.Key}: {string.Join(", ", header.Value)}");
                }
                Console.WriteLine("```");
            }

            var response = await client.SendAsync(request);

            if (showResHeaders)
            {
                // è®°å½•å“åº”å¤´
                Console.WriteLine("\nResponse Headers:");
                Console.WriteLine("```json\n");
                foreach (var header in response.Headers)
                {
                    Console.WriteLine($"{header.Key}: {string.Join(", ", header.Value)}");
                }
                Console.WriteLine("```");
            }

            var body = await response.Content.ReadAsStringAsync();

            Console.WriteLine("\nResponse Body:");
            var obj = JsonConvert.DeserializeObject(body);
            Console.WriteLine("```json \n" + JsonConvert.SerializeObject(obj, Formatting.Indented) + "\n```");
        }
        #endregion
    }
}

