                // åˆ›å»ºä¸€ä¸ª TcpListener å®ä¾‹ï¼Œå°è¯•ç»‘å®šåˆ°æŒ‡å®šç«¯å£
                TcpListener listener = new TcpListener(IPAddress.Loopback, port);
                listener.Start(); // å°è¯•å¯åŠ¨ç›‘å¬
                listener.Stop();  // æˆåŠŸå¯åŠ¨ååœæ­¢ç›‘å¬ï¼Œè¡¨ç¤ºç«¯å£å¯ç”¨
                return port; // å¦‚æœæ²¡æœ‰æŠ›å‡ºå¼‚å¸¸ï¼Œè¯´æ˜ç«¯å£å¯ç”¨ï¼Œè¿”å›è¯¥ç«¯å£
            }
            catch (SocketException)
            {
                // å¦‚æœç«¯å£è¢«å ç”¨ï¼Œä¼šæŠ›å‡º SocketExceptionï¼Œæˆ‘ä»¬ç»§ç»­å°è¯•ä¸‹ä¸€ä¸ªç«¯å£
                continue;
            }
        }

        throw new InvalidOperationException("No available port found.");
    }
}

