            };

            var root = new DockPanel();
            DockPanel.SetDock(topPanel, Dock.Top);
            root.Children.Add(topPanel);
            root.Children.Add(border);

            Content = root;

            _startBtn.Click += async (_, _) => await OnStartClickedAsync();
            _cancelBtn.Click += (_, _) => CancelScan();
            _copyBtn.Click += async (_, _) => await CopyResultsAsync();
            _clearBtn.Click += (_, _) => ClearResults();

            Closed += (_, _) => _cts?.Cancel();
        }

        private void ClearResults()
        {
            _results.Clear();
            _items.Clear();               // âœ… ä¿®æ”¹é›†åˆæœ¬èº«ï¼Œè€Œä¸æ˜¯ç»™ Items èµ‹å€¼
            _progress.Value = 0;
        }

        private async Task CopyResultsAsync()
        {
            var txt = string.Join(Environment.NewLine, _results.Select(r => r.ToLine()));
            var top = TopLevel.GetTopLevel(this); // âœ… Avalonia 11ï¼šä» TopLevel è·å– Clipboard
            if (top?.Clipboard is null) return;

            try
            {
                await top.Clipboard.SetTextAsync(txt);
            }
            catch
            {
                // ignore
            }
        }

        private void CancelScan()
        {
            _cts?.Cancel();
            _cancelBtn.IsEnabled = false;
        }

        private void RefreshListSnapshot()
        {
            // ä¸ºç®€å•èµ·è§ï¼Œè¿™é‡Œåˆ·æ–°å…¨é‡å¿«ç…§ï¼›ä¹Ÿå¯æŒ‰ç´¢å¼•å¢é‡æ›´æ–°ï¼ˆå·²åœ¨ä¸‹æ–¹å®ç°ï¼‰
            _items.Clear();
            foreach (var r in _results) _items.Add(r.ToLine());
        }

        private bool TryParseInputs(out string host, out int startPort, out int endPort, out int concurrency, out int timeoutMs, out string error)
        {
            error = string.Empty;
            host = _hostBox.Text?.Trim() ?? string.Empty;
            if (string.IsNullOrEmpty(host))
            {
                error = "Host is empty.";
                startPort = endPort = concurrency = timeoutMs = 0;
                return false;
            }

            if (!int.TryParse(_startPortBox.Text, out startPort) || startPort < 1 || startPort > 65535)
            {
                error = "Invalid start port (1-65535).";
                endPort = concurrency = timeoutMs = 0;
                return false;
            }

            if (!int.TryParse(_endPortBox.Text, out endPort) || endPort < 1 || endPort > 65535)
            {
                error = "Invalid end port (1-65535).";
                concurrency = timeoutMs = 0;
                return false;
            }

            if (endPort < startPort)
            {
                error = "End port must be >= start port.";
                concurrency = timeoutMs = 0;
                return false;
            }

            if (!int.TryParse(_concurrencyBox.Text, out concurrency) || concurrency < 1 || concurrency > 2000)
            {
                error = "Invalid concurrency (1-2000).";
                timeoutMs = 0;
                return false;
            }

            if (!int.TryParse(_timeoutBox.Text, out timeoutMs) || timeoutMs < 1 || timeoutMs > 10000)
            {
                error = "Invalid timeout (1-10000 ms).";
                return false;
            }

            return true;
        }

        private async Task OnStartClickedAsync()
        {
            if (!TryParseInputs(out var host, out var startPort, out var endPort, out var concurrency, out var timeoutMs, out var err))
            {
                await ShowMessageAsync("Input Error", err);
                return;
            }

            _startBtn.IsEnabled = false;
            _cancelBtn.IsEnabled = true;
            _results.Clear();
            _items.Clear();

            _cts?.Cancel();
            _cts = new CancellationTokenSource();

            try
            {
                await RunScanAsync(host, startPort, endPort, concurrency, timeoutMs, _cts.Token);
            }
            catch (OperationCanceledException)
            {
                await ShowMessageAsync("Info", "Scan cancelled.");
            }
            catch (Exception ex)
            {
                await ShowMessageAsync("Error", $"Unexpected error: {ex.Message}");
            }
            finally
            {
                _startBtn.IsEnabled = true;
                _cancelBtn.IsEnabled = false;
            }
        }

        private async Task ShowMessageAsync(string title, string message)
        {
            await Dispatcher.UIThread.InvokeAsync(() =>
            {
                var dlg = new Window
                {
                    Title = title,
                    Width = 460,
                    Height = 160,
                    WindowStartupLocation = WindowStartupLocation.CenterOwner,
                    Content = new StackPanel
                    {
                        Margin = new Thickness(12),
                        Children =
                        {
                            new TextBlock { Text = message, TextWrapping = TextWrapping.Wrap, Margin = new Thickness(0,0,0,8) },
                            new Button
                            {
                                Content = "OK",
                                HorizontalAlignment = HorizontalAlignment.Right,
                                Width = 80
                            }
                        }
                    }
                };

                if (dlg.Content is StackPanel sp && sp.Children.LastOrDefault() is Button ok)
                    ok.Click += (_, _) => dlg.Close();

                dlg.ShowDialog(this);
            });
            await Task.Delay(10);
        }

        private async Task RunScanAsync(string host, int startPort, int endPort, int concurrency, int timeoutMs, CancellationToken token)
        {
            var ports = Enumerable.Range(startPort, endPort - startPort + 1).ToList();
            var total = ports.Count;
            _progress.Minimum = 0;
            _progress.Maximum = total;
            _progress.Value = 0;

            var sem = new SemaphoreSlim(concurrency);
            var tasks = new List<Task>();

            // é¢„å¡«å……ç»“æœä¸å¯è§‚å¯Ÿé›†åˆï¼ˆé¿å…åç»­é¢‘ç¹ Addï¼‰
            foreach (var p in ports)
            {
                _results.Add(new PortResult { Port = p, Status = "Pending" });
                _items.Add($"{p}\tPending");
            }

            for (int i = 0; i < ports.Count; i++)
            {
                token.ThrowIfCancellationRequested();
                await sem.WaitAsync(token).ConfigureAwait(false);

                var index = i;
                var port = ports[index];

                var t = Task.Run(async () =>
                {
                    try
                    {
                        var status = await ProbeTcpPortAsync(host, port, timeoutMs, token).ConfigureAwait(false);

                        await Dispatcher.UIThread.InvokeAsync(() =>
                        {
                            _results[index].Status = status;
                            // å¢é‡åˆ·æ–°ï¼šç›´æ¥æ”¹ ObservableCollection å¯¹åº”é¡¹
                            _items[index] = _results[index].ToLine();
                            _progress.Value += 1;
                        });
                    }
                    catch (OperationCanceledException)
                    {
                        await Dispatcher.UIThread.InvokeAsync(() =>
                        {
                            _results[index].Status = "Cancelled";
                            _items[index] = _results[index].ToLine();
                            _progress.Value += 1;
                        });
                    }
                    catch (Exception ex)
                    {
                        await Dispatcher.UIThread.InvokeAsync(() =>
                        {
                            _results[index].Status = $"Error:{ex.Message}";
                            _items[index] = _results[index].ToLine();
                            _progress.Value += 1;
                        });
                    }
                    finally
                    {
                        sem.Release();
                    }
                }, token);

                tasks.Add(t);
            }

            await Task.WhenAll(tasks).ConfigureAwait(false);
        }

        private static async Task<string> ProbeTcpPortAsync(string host, int port, int timeoutMs, CancellationToken token)
        {
            using var client = new TcpClient();
            var connectTask = client.ConnectAsync(host, port);
            var delayTask = Task.Delay(timeoutMs, token);
            var finished = await Task.WhenAny(connectTask, delayTask).ConfigureAwait(false);

            if (finished == delayTask)
                return "Filtered/TimedOut";

            try
            {
                await connectTask;
                try { client.Close(); } catch { }
                return "Open";
            }
            catch (SocketException)
            {
                return "Closed";
            }
            catch (OperationCanceledException)
            {
                throw;
            }
            catch (Exception ex)
            {
                return $"Err:{ex.GetType().Name}";
            }
        }
    }
}

