using System;
using System.Collections.Generic;

class Program
{
    static void Main()
    {
        // Console.WriteLine("=== æ—§å†™æ³•ï¼šä¼ ç»Ÿæ‰©å±•æ–¹æ³• ===");
        var list1 = new List<int> { 1, 2, 3 };
        Console.WriteLine($"IsEmpty (æ–¹æ³•è°ƒç”¨): {list1.IsEmptyMethod()}");
        var filtered1 = list1.WhereOld(x => x > 1);
        Console.WriteLine($"Where è¿‡æ»¤: [{string.Join(", ", filtered1)}]");

        Console.WriteLine("\n=== æ–°å†™æ³•ï¼šæ‰©å±•å±æ€§ ===");
        Console.WriteLine($"IsEmpty (å±æ€§è®¿é—®): {list1.IsEmpty}");
        var filtered2 = list1.WhereNew(x => x > 1);
        Console.WriteLine($"Where è¿‡æ»¤: [{string.Join(", ", filtered2)}]");

        Console.WriteLine("\n=== æ–°å†™æ³•ï¼šé™æ€æ‰©å±• ===");
        var list2 = new List<int> { 4, 5, 6 };
        var combined = List<int>.Combine(list1, list2);
        Console.WriteLine($"é™æ€æ–¹æ³• Combine: [{string.Join(", ", combined)}]");

        var empty = List<int>.Empty;
        Console.WriteLine($"é™æ€å±æ€§ Empty: é•¿åº¦={empty.Count}");

        Console.WriteLine("\n=== æ–°å†™æ³•ï¼šè¿ç®—ç¬¦é‡è½½ ===");
        var added = list1 + list2;
        Console.WriteLine($"è¿ç®—ç¬¦ +: [{string.Join(", ", added)}]");
    }
}

// æ—§å†™æ³•ï¼ˆä¼ ç»Ÿæ‰©å±•æ–¹æ³•ï¼Œåªèƒ½æ‰©å±•å®ä¾‹æ–¹æ³•ï¼‰
static class OldListExtensions
{
    public static bool IsEmptyMethod<T>(this List<T> list)
    {
        return list.Count == 0;
    }

    public static List<T> WhereOld<T>(this List<T> list, Func<T, bool> predicate)
    {
        return list.FindAll(x => predicate(x));
    }
}

// æ–°å†™æ³•ï¼ˆC# 14: æ‰©å±•æˆå‘˜ï¼Œæ”¯æŒæ‰©å±•å±æ€§å’Œé™æ€æ‰©å±•ï¼‰
static class NewListExtensions
{
    // å®ä¾‹æ‰©å±•å—
    extension<T>(List<T> list)
    {
        // æ‰©å±•å±æ€§
        public bool IsEmpty => list.Count == 0;

        // æ‰©å±•æ–¹æ³•
        public List<T> WhereNew(Func<T, bool> predicate)
        {
            return list.FindAll(x => predicate(x));
        }
    }

    // é™æ€æ‰©å±•å—
    extension<T>(List<T>)
    {
        // é™æ€æ‰©å±•æ–¹æ³•
        public static List<T> Combine(List<T> first, List<T> second)
        {
            var result = new List<T>(first);
            result.AddRange(second);
            return result;
        }

        // é™æ€æ‰©å±•å±æ€§
        public static List<T> Empty => new List<T>();

        // ç”¨æˆ·è‡ªå®šä¹‰è¿ç®—ç¬¦
        public static List<T> operator +(List<T> left, List<T> right)
        {
            var result = new List<T>(left);
            result.AddRange(right);
            return result;
        }
    }
}

