using System;

class Program
{
    class Order 
    { 
        public string Id; 
        public override string ToString() => $"Order({Id})";
    }

    class Customer 
    { 
        public Order Order; 
    }

    class Counter 
    { 
        public int Value; 
    }

    class Tracker 
    { 
        public Counter Counter; 
    }

    static Order GetCurrentOrder() => new Order { Id = "ORD-123" };

    // ============================
    // ç¤ºä¾‹ 1: ç®€å•èµ‹å€¼
    // ============================

    // æ—§å†™æ³•ï¼ˆéœ€è¦æ˜¾å¼ç©ºæ£€æŸ¥ï¼‰
    static void OldAssignOrder(Customer customer)
    {
        if (customer is not null)
