            customer.Order = GetCurrentOrder();
        }
    }

    // æ–°å†™æ³•ï¼ˆC# 14: ç©ºæ¡ä»¶èµ‹å€¼ï¼‰
    static void NewAssignOrder(Customer customer)
    {
        customer?.Order = GetCurrentOrder();
    }

    // ============================
    // ç¤ºä¾‹ 2: å¤åˆèµ‹å€¼
    // ============================

    // æ—§å†™æ³•
    static void OldIncrementCounter(Tracker tracker)
    {
        if (tracker is not null && tracker.Counter is not null)
        {
            tracker.Counter.Value += 10;
        }
    }

    // æ–°å†™æ³•ï¼ˆC# 14: ç©ºæ¡ä»¶å¤åˆèµ‹å€¼ï¼‰
    static void NewIncrementCounter(Tracker tracker)
    {
        tracker?.Counter.Value += 10;
    }

    static void Main()
    {
        Console.WriteLine("=== ç¤ºä¾‹ 1: ç®€å•èµ‹å€¼ ===");

        Console.WriteLine("\næ—§å†™æ³•:");
        var customer1 = new Customer();
        Console.WriteLine($"èµ‹å€¼å‰: {customer1.Order}");
        OldAssignOrder(customer1);
        Console.WriteLine($"èµ‹å€¼å: {customer1.Order}");

        Console.WriteLine("\nå¯¹ null å¯¹è±¡è°ƒç”¨:");
        OldAssignOrder(null);
        Console.WriteLine("ä¸ä¼šæŠ›å‡ºå¼‚å¸¸");

        Console.WriteLine("\næ–°å†™æ³•:");
        var customer2 = new Customer();
        Console.WriteLine($"èµ‹å€¼å‰: {customer2.Order}");
        NewAssignOrder(customer2);
        Console.WriteLine($"èµ‹å€¼å: {customer2.Order}");

        Console.WriteLine("\nå¯¹ null å¯¹è±¡è°ƒç”¨:");
        NewAssignOrder(null);
        Console.WriteLine("ä¸ä¼šæŠ›å‡ºå¼‚å¸¸ï¼Œä¹Ÿä¸ä¼šè°ƒç”¨ GetCurrentOrder()");

        Console.WriteLine("\n=== ç¤ºä¾‹ 2: å¤åˆèµ‹å€¼ ===");

        Console.WriteLine("\næ—§å†™æ³•:");
        var tracker1 = new Tracker { Counter = new Counter { Value = 5 } };
        Console.WriteLine($"å¢åŠ å‰: {tracker1.Counter.Value}");
        OldIncrementCounter(tracker1);
        Console.WriteLine($"å¢åŠ å: {tracker1.Counter.Value}");

        Console.WriteLine("\nå¯¹ null å¯¹è±¡è°ƒç”¨:");
        OldIncrementCounter(null);
        Console.WriteLine("ä¸ä¼šæŠ›å‡ºå¼‚å¸¸");

        Console.WriteLine("\næ–°å†™æ³•:");
        var tracker2 = new Tracker { Counter = new Counter { Value = 5 } };
        Console.WriteLine($"å¢åŠ å‰: {tracker2.Counter.Value}");
        NewIncrementCounter(tracker2);
        Console.WriteLine($"å¢åŠ å: {tracker2.Counter.Value}");

        Console.WriteLine("\nå¯¹ null å¯¹è±¡è°ƒç”¨:");
        NewIncrementCounter(null);
        Console.WriteLine("ä¸ä¼šæŠ›å‡ºå¼‚å¸¸");

        Console.WriteLine("\n=== å…¶ä»–å¤åˆè¿ç®—ç¬¦ç¤ºä¾‹ ===");
        var tracker3 = new Tracker { Counter = new Counter { Value = 100 } };
        Console.WriteLine($"åˆå§‹å€¼: {tracker3.Counter.Value}");

        tracker3?.Counter.Value -= 20;
        Console.WriteLine($"-= å: {tracker3.Counter.Value}");

        tracker3?.Counter.Value *= 2;
        Console.WriteLine($"*= å: {tracker3.Counter.Value}");

        tracker3?.Counter.Value /= 4;
        Console.WriteLine($"/= å: {tracker3.Counter.Value}");
    }
}

