    }

    // åœºæ™¯ 1: æ—¥å¿—æ–¹æ³•
    static void LogTypeInfo<T>()
    {
        // æ–°å†™æ³•ï¼šç›´æ¥ä½¿ç”¨æœªç»‘å®šæ³›å‹
        Console.WriteLine($"å¤„ç†ç±»å‹: {nameof(List<>)}<{typeof(T).Name}>");
