    // åœºæ™¯ 2: å¼‚å¸¸å¤„ç†
    static void ThrowExceptionOld<T>()
    {
        // æ—§å†™æ³•ï¼šéœ€è¦ä½¿ç”¨å°é—­æ³›å‹
        throw new InvalidOperationException(
            $"{nameof(List<T>)} æ“ä½œå¤±è´¥");
    }

    static void ThrowExceptionNew<T>()
    {
        // æ–°å†™æ³•ï¼šä½¿ç”¨æœªç»‘å®šæ³›å‹
        throw new InvalidOperationException(
            $"{nameof(List<>)} æ“ä½œå¤±è´¥");
    }
}

