    // ============================
    // ç¤ºä¾‹ 4: Span å’Œ ReadOnlySpan äº’è½¬
    // ============================

    static void SpanConversions()
    {
        int[] array = { 10, 20, 30, 40, 50 };

        // æ—§å†™æ³•
        Console.WriteLine("\næ—§å†™æ³•è½¬æ¢:");
        Span<int> span1 = new Span<int>(array);
        ReadOnlySpan<int> roSpan1 = new ReadOnlySpan<int>(array);
        Console.WriteLine($"Span -> array: éœ€è¦æ˜¾å¼è½¬æ¢");

        // æ–°å†™æ³•
        Console.WriteLine("\næ–°å†™æ³•è½¬æ¢:");
        Span<int> span2 = array;              // éšå¼è½¬æ¢
        ReadOnlySpan<int> roSpan2 = array;    // éšå¼è½¬æ¢
        ReadOnlySpan<int> roSpan3 = span2;    // Span<T> -> ReadOnlySpan<T>
        Console.WriteLine($"array -> Span: éšå¼");
        Console.WriteLine($"array -> ReadOnlySpan: éšå¼");
        Console.WriteLine($"Span -> ReadOnlySpan: éšå¼");
    }

    static void Main()
