static class OldSpanExtensions
{
    public static int Sum(ReadOnlySpan<int> span)
    {
        int sum = 0;
        foreach (var item in span)
            sum += item;
        return sum;
    }
}

// æ–°å†™æ³•ï¼ˆC# 14: Span å¯ä»¥ä½œä¸ºæ‰©å±•æ–¹æ³•æ¥æ”¶å™¨ï¼‰
static class NewSpanExtensions
{
    public static int Sum(this ReadOnlySpan<int> span)
    {
        int sum = 0;
        foreach (var item in span)
            sum += item;
        return sum;
    }

    public static double Average(this ReadOnlySpan<int> span)
    {
        if (span.Length == 0) return 0;
        return (double)span.Sum() / span.Length;
    }
}

