// 8. ç”¨æˆ·è‡ªå®šä¹‰å¤åˆèµ‹å€¼è¿ç®—ç¬¦
// æ–‡ä»¶å: 08_UserDefinedCompoundAssignment.cs
// ============================
using System;

class Program
{
    // ============================
    // ç¤ºä¾‹ 1: å‘é‡ç±»å‹
    // ============================

    // æ—§å†™æ³•ï¼ˆåªèƒ½å®šä¹‰ + è¿ç®—ç¬¦ï¼Œå¤åˆèµ‹å€¼è‡ªåŠ¨å±•å¼€ä¸º a = a + bï¼‰
    struct OldVector
    {
        public double X, Y;

        public OldVector(double x, double y)
        {
            X = x;
            Y = y;
        }

        public static OldVector operator +(OldVector a, OldVector b)
        {
            return new OldVector(a.X + b.X, a.Y + b.Y);
        }

        public static OldVector operator -(OldVector a, OldVector b)
        {
            return new OldVector(a.X - b.X, a.Y - b.Y);
        }

        public static OldVector operator *(OldVector v, double scalar)
        {
            return new OldVector(v.X * scalar, v.Y * scalar);
        }

        public override string ToString() => $"({X:F2}, {Y:F2})";
    }

    // æ–°å†™æ³•ï¼ˆC# 14: ç”¨æˆ·è‡ªå®šä¹‰å¤åˆèµ‹å€¼è¿ç®—ç¬¦ - å®ä¾‹æ–¹æ³•ï¼Œvoid è¿”å›ï¼‰
    struct NewVector
    {
        public double X, Y;

        public NewVector(double x, double y)
        {
            X = x;
            Y = y;
        }

        // åŸºæœ¬è¿ç®—ç¬¦ï¼ˆé™æ€æ–¹æ³•ï¼‰
        public static NewVector operator +(NewVector a, NewVector b)
        {
            return new NewVector(a.X + b.X, a.Y + b.Y);
        }

        public static NewVector operator -(NewVector a, NewVector b)
        {
            return new NewVector(a.X - b.X, a.Y - b.Y);
        }

        public static NewVector operator *(NewVector v, double scalar)
        {
            return new NewVector(v.X * scalar, v.Y * scalar);
        }

        // C# 14: å¤åˆèµ‹å€¼è¿ç®—ç¬¦ï¼ˆå®ä¾‹æ–¹æ³•ï¼Œvoid è¿”å›ï¼ŒåŸåœ°ä¿®æ”¹ï¼‰
        public void operator +=(NewVector other)
        {
            X += other.X;
            Y += other.Y;
        }

        public void operator -=(NewVector other)
        {
            X -= other.X;
            Y -= other.Y;
        }

        public void operator *=(double scalar)
        {
            X *= scalar;
            Y *= scalar;
        }

        public override string ToString() => $"({X:F2}, {Y:F2})";
    }

    // ============================
    // ç¤ºä¾‹ 2: å¤æ•°ç±»å‹
    // ============================

    struct Complex
    {
        public double Real, Imaginary;

        public Complex(double real, double imaginary)
        {
            Real = real;
            Imaginary = imaginary;
        }

        // åŸºæœ¬è¿ç®—ç¬¦
        public static Complex operator +(Complex a, Complex b)
        {
            return new Complex(a.Real + b.Real, a.Imaginary + b.Imaginary);
        }

        public static Complex operator *(Complex a, Complex b)
        {
            return new Complex(
                a.Real * b.Real - a.Imaginary * b.Imaginary,
                a.Real * b.Imaginary + a.Imaginary * b.Real
            );
        }

        // C# 14: å¤åˆèµ‹å€¼è¿ç®—ç¬¦ï¼ˆåŸåœ°ä¿®æ”¹ï¼Œæå‡æ€§èƒ½ï¼‰
        public void operator +=(Complex other)
        {
            Real += other.Real;
            Imaginary += other.Imaginary;
        }

        public void operator *=(Complex other)
        {
            double newReal = Real * other.Real - Imaginary * other.Imaginary;
            double newImag = Real * other.Imaginary + Imaginary * other.Real;
            Real = newReal;
            Imaginary = newImag;
        }

        public override string ToString() =>
            $"{Real:F2} {(Imaginary >= 0 ? "+" : "")}{Imaginary:F2}i";
    }

