    struct Matrix2x2
    {
        public double M11, M12, M21, M22;

        public Matrix2x2(double m11, double m12, double m21, double m22)
        {
            M11 = m11; M12 = m12;
            M21 = m21; M22 = m22;
        }

        // åŸºæœ¬è¿ç®—ç¬¦
        public static Matrix2x2 operator +(Matrix2x2 a, Matrix2x2 b)
        {
            return new Matrix2x2(
                a.M11 + b.M11, a.M12 + b.M12,
                a.M21 + b.M21, a.M22 + b.M22
            );
        }

        public static Matrix2x2 operator *(Matrix2x2 a, Matrix2x2 b)
        {
            return new Matrix2x2(
                a.M11 * b.M11 + a.M12 * b.M21,
                a.M11 * b.M12 + a.M12 * b.M22,
                a.M21 * b.M11 + a.M22 * b.M21,
                a.M21 * b.M12 + a.M22 * b.M22
            );
        }

        // C# 14: å¤åˆèµ‹å€¼è¿ç®—ç¬¦
        public void operator +=(Matrix2x2 other)
        {
            M11 += other.M11; M12 += other.M12;
            M21 += other.M21; M22 += other.M22;
        }

        public void operator *=(Matrix2x2 other)
        {
            var result = this * other;
            this = result;
        }

        public override string ToString() =>
            $"[{M11:F1} {M12:F1}]\n[{M21:F1} {M22:F1}]";
    }

