        public static Backpack operator +(Backpack oldBackpack, float volume)
        {
            var newBackpack = new Backpack(oldBackpack.Capacity)
            {
                Volume = oldBackpack.Volume + volume,
                Weight = oldBackpack.Weight + volume * 0.5f
            };
            return newBackpack;
        }

        // C# 14: += è¿ç®—ç¬¦ç›´æ¥ä¿®æ”¹å½“å‰å®ä¾‹
        public void operator +=(float volume)
        {
            if (Volume + volume > Capacity)
