                throw new InvalidOperationException("èƒŒåŒ…ç©ºé—´ä¸è¶³");
            }
            Volume += volume;
            Weight += volume * 0.5f;
        }

        public override string ToString() =>
            $"å®¹é‡: {Volume:F1}/{Capacity:F1}, é‡é‡: {Weight:F1}kg";
    }

